import { useRef } from "react";
import classes from "./Checkout.module.css";

const isEmpty = (value) => value.trim() === "";
const isNotFiveChars = (value) => value.trim().length !== 5;

const Checkout = (props) => {
  const nameInputRef = useRef();
  const addressInputRef = useRef();
  const cpInputRef = useRef();
  const cityInputRef = useRef();

  const confirmHandler = (event) => {
    event.preventDefault();

    const enteredName = nameInputRef.current.value;
    const enteredAddress = addressInputRef.current.value;
    const enteredPostalCode = cpInputRef.current.value;
    const enteredCity = cityInputRef.current.value;

    const nameIsValid = !isEmpty(enteredName);
    const addressIsValid = !isEmpty(enteredAddress);
    const PostalCodeIsValid = !isNotFiveChars(enteredPostalCode);
    const cityIsValid = !isEmpty(enteredCity);

    const formIsValid =
      nameIsValid && addressIsValid && PostalCodeIsValid && cityIsValid;
  };

  return (
    <form onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name" ref={nameInputRef}>
          Your name
        </label>
        <input type="text" id="name"></input>
      </div>
      <div className={classes.control}>
        <label htmlFor="address" ref={addressInputRef}>
          Address
        </label>
        <input type="text" id="address"></input>
      </div>
      <div className={classes.control}>
        <label htmlFor="cp" ref={cpInputRef}>
          Postal Code
        </label>
        <input type="text" id="cp"></input>
      </div>
      <div className={classes.control}>
        <label htmlFor="city" ref={cityInputRef}>
          City
        </label>
        <input type="text" id="city"></input>
      </div>
      <div className={classes.actions}>
        <button onClick={props.onCancel} type="button">
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
